(function(){Vue.component("convos-dialog-settings",{props:["dialog","user"],computed:{participants:function(){if(!this.dialog)return[];return Object.keys(this.dialog.participants)
.sort(function(a,b){return a.toLowerCase().localeCompare(b.toLowerCase());})
.map(function(k){return this.dialog.participants[k]||{name:k};}.bind(this));}},methods:{shareUrl:function(){var dialog=this.dialog;return['','api','connection',dialog.connection_id,'dialog',dialog.dialog_id,'share']
.map(function(p){return encodeURIComponent(p)})
.join('/');}},template:"<div class=\"row\">\n    <div class=\"col s12\">\n      <h5>About {{dialog.name}}</h5>\n      <p v-if=\"!dialog.is_private\">{{{dialog.topic.rich() || &quot;No topic is set.&quot;}}}</p>\n      <p v-if=\"dialog.is_private\">This is a private conversation.</p>\n    </div>\n  </div>\n  <div class=\"menu-item\">\n    <a @click.prevent=\"send(&#39;/close&#39;)\" href=\"#close\">\n      <i class=\"material-icons right\">close</i>\n      {{dialog.is_private ? &#39;Close conversation&#39; : &#39;Part channel&#39;}}\n    </a>\n  </div>\n  <div class=\"menu-item\" v-if=\"settings.share_dialog\">\n    <a :href=\"shareUrl()\" target=\"_blank\">\n      <i class=\"material-icons right\">share</i>\n      Share conversation\n    </a>\n  </div>\n  <div class=\"row\">\n    <div class=\"col s12\">\n      <h5>Participants ({{participants.length}})</h5>\n    </div>\n  </div>\n  <div class=\"menu-item\" v-for=\"p in participants\">\n    <span class=\"right\">\n      {{p.mode ? &#39;+&#39; + p.mode : &#39;&#39;}}\n      <a @click.prevent=\"send(&#39;/whois &#39; + p.name)\" class=\"waves-effect waves-light\" href=\"#info\"><i class=\"material-icons\">info</i></a>\n    </span>\n    <a @click.prevent=\"send(&#39;/query &#39; + p.name)\" href=\"#chat:{{p.name}}\">{{p.name}}</a>\n  </div>\n"})})();