(function(){Vue.component("convos-message-participants",{props:["dialog","msg","user"],data:function(){var participants=Object.$values(this.dialog.participants).sort(function(a,b){if(a.name.toLowerCase()<b.name.toLowerCase())return-1;if(a.name.toLowerCase()>b.name.toLowerCase())return 1;return 0;});return{n:participants.length,nParticipants:String.prototype.numberAsString.call(participants.length),participants:participants};},methods:{query:function(p){this.dialog.connection().send("/query "+p.name);}},template:"<div class=\"convos-message notice\">\n    <span class=\"secondary-content ts\" v-tooltip=\"msg.ts.toLocaleString()\">{{msg.ts | timestring}}</span>\n    <a class=\"title\" href=\"#chat\">{{msg.from}}</a>\n    <div class=\"message\">\n      <template v-if=\"n &gt; 1\">{{nParticipants | capitalize}} participants</template>\n      <template v-else>You are the only participant</template>\n      in {{dialog.name}}, connected to {{dialog.connection().name}}{{n &gt; 1 ? &quot;:&quot; : &quot;.&quot;}}\n      <template v-for=\"p in participants\">\n        <a @click.prevent=\"query(p)\" href=\"#query:{{p.name}}\">{{p.mode}}{{p.name}}</a>{{$index + 2 == n ? &quot; and &quot; : $index + 1 == n ? &quot;&quot; : &quot;, &quot;}}</template>.\n    </div>\n  </div>\n"})})();