(function(){Vue.component("convos-settings",{props:["error","user"],computed:{show:function(){return this.settings.main.indexOf("#help")==0?"help":this.settings.main.indexOf("#profile")==0?"profile":!this.user.connections.length?"wizard":this.settings.main.indexOf("#connection")==0?"add_connection":this.settings.main.indexOf("#create-dialog")==0?"create_dialog":"error";}},methods:{fillIn:function(){this.$refs.settings.server=this.settings.default_server||"chat.freenode.net:6697";this.$refs.settings.onConnectCommands="/join #convos";}},template:"<div class=\"convos-settings\">\n    <header>\n      <convos-toggle-main-menu :user=\"user\"></convos-toggle-main-menu>\n      <h2 v-tooltip.literal=\"Welcome to Convos!\">Convos</h2>\n      <convos-header-links :toggle=\"true\" :user=\"user\"></convos-header-links>\n    </header>\n    <main v-if=\"show == &#39;error&#39;\">\n      <div class=\"row\">\n        <div class=\"col s12\">\n          <h4>{{error.title || &quot;Not found&quot;}}</h4>\n          <p>{{error.message}}</p>\n        </div>\n      </div>\n    </main>\n    <main v-if=\"show == &#39;wizard&#39;\">\n      <div class=\"row\">\n        <div class=\"col s12\">\n          <h4>Welcome to Convos!</h4>\n          <p>\n            Convos is the simplest way to use IRC. It is always online,\n            and accessible in your web browser, both on desktop and mobile.\n          </p>\n          <p>\n            Before you can start chatting, you need to create a connection.\n            You can add more connections later on if you need.\n          </p>\n          <p v-if=\"settings.default_server\">\n            If you don&#39;t have any special preferences, you can just hit &quot;Create&quot; to get started.\n          </p>\n          <p v-if=\"!settings.default_server\">\n            Just fill in a <a @click.prevent=\"fillIn\" href=\"#fillin\">server name</a>\n            and hit &quot;Create&quot; to get started.\n          </p>\n        </div>\n      </div>\n      <convos-connection-settings :user=\"user\" v-ref:settings></convos-connection-settings>\n    </main>\n    <main v-if=\"show == &#39;add_connection&#39;\">\n      <div class=\"row\">\n        <div class=\"col s12\">\n          <h4>Add connection</h4>\n        </div>\n      </div>\n      <convos-connection-settings :connection=\"null\" :user=\"user\"></convos-connection-settings>\n    </main>\n    <main v-if=\"show == &#39;create_dialog&#39;\">\n      <convos-create-dialog :user=\"user\"></convos-create-dialog>\n    </main>\n    <main v-if=\"show == &#39;help&#39;\">\n      <convos-help :user=\"user\"></convos-help>\n    </main>\n    <main v-if=\"show == &#39;profile&#39;\">\n      <convos-profile :user=\"user\"></convos-profile>\n    </main>\n  </div>\n"})})();